import React, { useState } from "react";
import Card from "../UI/Card";
import classes from "./AddUser.module.css";
import Button from "../UI/Button";

const AddUser = () => {
  const [id, setID] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [gender, setGender] = useState("");
  const [DateOfBirth, setDateofBirth] = useState("");

  const FirstNameChangeHandler = (event) => {
    setFirstName(event.target.value);
  };
  const LastNameChangeHandler = (event) => {
    setLastName(event.target.value);
  };
  const EmailChangeHandler = (event) => {
    setEmail(event.target.value);
  };
  const GenderChangeHandler = (event) => {
    setGender(event.target.value);
  };
  const DateofBirthChangeHandler = (event) => {
    setDateofBirth(event.target.value);
  };


  

  return (
    <Card className={classes.input}>
      <form>
        <label htmlFor="FirstName">FirstName</label>
        <input onChange={FirstNameChangeHandler} id="FirstName" type="text" />
        <label htmlFor="LastName">LastName</label>
        <input onChange={LastNameChangeHandler} id="LastName" type="text" />
        <label htmlFor="Email">Email</label>
        <input onChange={EmailChangeHandler} id="Email" type="text" />
        <label htmlFor="Gender">Gender</label>
        <input onChange={GenderChangeHandler} id="Gender" type="text" />
        <label htmlFor="DateofBirth">Date of Birth</label>
        <input
          onChange={DateofBirthChangeHandler}
          id="DateofBirth"
          type="date"
        />
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
