import React, { useEffect, useState } from "react";
// import UserData from "../Post/MOCK_DATA.json";
import style from "./UsersList.module.css";
import Card from "../UI/Card";
import Button from "../UI/Button";
import { Fragment } from "react";
import ReadOnlyRow from "./UserEdit/ReadOnlyRow";
import EditableOnlyRow from "./UserEdit/EditableRow";

function SearchUserListEditable(props) {
  const [data, setData] = useState(null);
  const [isPending, setIsPending] = useState(true);
  const [error, setError] = useState(null);

  const [isediting, setIsediting] = useState();
  const [name, setName] = useState("");
  const [foundUsers, setFoundUsers] = useState(props.Users);
  const [rowEditing, setRowEditing] = useState(null);

  const [editFormData, setEditFormData] = useState({
    first_name: "",
    last_name: "",
    time: "",
    email: "",
    Job: "",
    DateOfBirthat: "",
    donate: "",
  });

  const editFormChangeHandler = (event) => {
    const filedName = event.target.getAttribute("name");
    const fieldValue = event.target.value;

    const newFormData = { ...editFormData };
    newFormData[filedName] = fieldValue;
    setEditFormData(newFormData);
  };

  const editFormHandler = (editFormData) => {
    setRowEditing(editFormData.id);

    const formValues = {
      first_name: editFormData.first_name,
      last_name: editFormData.last_name,
      time: editFormData.time,
      email: editFormData.email,
      Job: editFormData.Job,
      DateOfBirthat: editFormData.DateOfBirth,
      donate: editFormData.donate,
    };
    setEditFormData(formValues);
  };

  const editFormSubmit = (event, user) => {
    event.preventDefault();
    setIsPending(true);

    const editUser = {
      id: rowEditing,
      first_name: editFormData.first_name,
      last_name: editFormData.last_name,
      time: editFormData.time,
      email: editFormData.email,
      Job: editFormData.Job,
      DateOfBirthat: editFormData.DateOfBirth,
      donate: editFormData.donate,
    };

    console.log(editUser);
    fetch("http://localhost:8000/AllUsers", {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(editUser),
    })
      .then(() => {
        setIsPending(false);
      setData(data);
        setError(null);
      })
       .catch((err) => {
        setIsPending(false);
        setError(err.message);
    });
  };
  // search by filter
  const filter = (event) => {
    const keyword = event.target.value;

    if (keyword !== "") {
      const results = props.Users.filter((user) => {
        return user.first_name.toLowerCase().startsWith(keyword.toLowerCase());
        // Use the toLowerCase() method to make it case-insensitive
      });
      setFoundUsers(results);
    } else {
      setFoundUsers(props.Users);
      // If the text field is empty, show all users
    }
    setName(keyword);
  };

  // toggle edit table
  const enableEdit = () => {
    setIsediting(true);
  };
  const disableEdit = () => {
    setIsediting(false);
  };

  return (
    <Card className={style.users}>
      <div className={style.tableContainer}>
        {foundUsers && (
          <h3>
            Currently Table Contain {foundUsers.length} Records
            {/* <br /> Total Selected Debt is {getFormattedPrice(total)} */}
          </h3>
        )}
        <input
          type="search"
          value={name}
          id={style.myInput}
          onChange={filter}
          placeholder="Search for names.."
        />
        <form onSubmit={editFormSubmit}>
          <table className={style.userTable}>
            <thead>
              <tr>
                <th>Avator</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Time</th>
                <th>Email</th>
                <th>JOB</th>
                <th>Birthday</th>
                <th>Donate</th>
                <th>
                  {isediting ? (
                    <Button onClick={disableEdit}>Done</Button>
                  ) : (
                    <Button onClick={enableEdit}>Edit</Button>
                  )}
                </th>
              </tr>
            </thead>
            <tbody>
              {foundUsers && foundUsers.length > 0 ? (
                foundUsers.map((user) =>
                  rowEditing === user.id ? (
                    <Fragment>
                      <EditableOnlyRow
                        user={user}
                        isediting={isediting}
                        Ondelete={props.Ondelete}
                        editFormData={editFormData}
                        editFormChangeHandler={editFormChangeHandler}
                      />
                    </Fragment>
                  ) : (
                    <Fragment>
                      <ReadOnlyRow
                        user={user}
                        isediting={isediting}
                        rowEditing={editFormHandler}
                        Ondelete={props.Ondelete}
                      />
                    </Fragment>
                  )
                )
              ) : (
                <h3>No results found!</h3>
              )}
              ;
            </tbody>
          </table>
        </form>
      </div>
    </Card>
  );
}

export default SearchUserListEditable;
