import React, { useEffect, useState } from "react";
import AddUser from "./Components/User/AddUser";
import SearchUserList from "./Components/User/SearchUserList";
// import UserList from "./Components/User/UserList";
// import UserData from "./Components/Post/MOCK_DATA.json";

function App() {
  // const [usersList, setUsersList] = useState(UserData);
  const [usersList, setUsersList] = useState(null);
  const [isPending, setIspending] = useState(true);

  useEffect(() => {
    fetch("http://localhost:8000/AllUserss")
      .then((res) => {
        if (!res.ok) {
          throw Error("Could not fetch the data for that resource");
        }
        return res.json();
      })
      .then((data) => {
        console.log(data);
        setUsersList(data);
        setIspending(false);
      })
      .catch((err) => {
        console.log(err.message);
      });
  }, []);

  const deleteHandler = (id) => {
    console.log(id);
    const newUserslist = usersList.filter((userlist) => userlist.id !== id);
    setUsersList(newUserslist);
  };

  return (
    <div>
      <AddUser />
      {isPending && <h2>Loading</h2>}
      {usersList && (
        <SearchUserList Users={usersList} Ondelete={deleteHandler} />
      )}
      {/* {usersList && <UserList Users={usersList} Ondelete={deleteHandler} />} */}
    </div>
  );
}

export default App;

