import React, { useEffect, useState } from "react";
import AddUser from "./Components/User/AddUser";
import UserList from "./Components/User/UserList";
import SearchUserList from "./Components/User/SearchList"
import UserData from "./Components/Post/MOCK_DATA.json";

function App() {
  const [usersList, setUsersList] = useState(UserData.AllUsers);

  useEffect(() => {
    fetch('http://localhost:8000/AllUsers')
    .then
  },[])

  const deleteHandler = (id) => {
    const newUserslist = usersList.filter((userlist) => userlist.id !== id);
    setUsersList(newUserslist);
  };

  return (
    <div>
      <AddUser />
      <SearchUserList Users={usersList} Ondelete={deleteHandler} />
      <UserList Users={usersList} Ondelete={deleteHandler} />
    </div>
  );
}

export default App;



