import React, { useEffect, useState } from "react";
// import UserData from "../Post/MOCK_DATA.json";
import style from "./UsersList.module.css";
import Card from "../UI/Card";
import Button from "../UI/Button";

const getFormattedPrice = (price) => `$${price.toFixed(2)}`;


function SearchUserList(props) {
  const [isediting, setIsediting] = useState();
  const [name, setName] = useState("");
  const [foundUsers, setFoundUsers] = useState(props.Users);

  const [checkedState, setCheckedState] = useState(
    new Array(props.Users.length).fill(false)
  );
  const [total, setTotal] = useState(0);
  // checkbox get total and more
  // map checkedState for items
  const handleOnChange = (position) => {
    const updatedCheckedState = checkedState.map((item, index) =>
      index === position ? !item : item
    );
    setCheckedState(updatedCheckedState);

    const totalPrice = updatedCheckedState.reduce(
      (sum, currentState, index) => {
        if (currentState === true) {
          return sum + props.Users[index].Debt;
        }
        return sum;
      },
      0
    );

    setTotal(totalPrice);
  };

  // console.log(props.Users.length);
  // console.log(foundUsers.length);
  useEffect(() => {
    setFoundUsers(props.Users);
  }, [isediting, props.Users]);

  // search by filter
  const filter = (event) => {
    const keyword = event.target.value;

    if (keyword !== "") {
      const results = props.Users.filter((user) => {
        return user.first_name.toLowerCase().startsWith(keyword.toLowerCase());
        // Use the toLowerCase() method to make it case-insensitive
      });
      setFoundUsers(results);
    } else {
      setFoundUsers(props.Users);
      // If the text field is empty, show all users
    }
    setName(keyword);
  };

  // toggle edit table
  const enableEdit = () => {
    setIsediting(true);
  };
  const disableEdit = () => {
    setIsediting(false);
  };

  return (
    <Card className={style.users}>
      <div>
        {foundUsers && (
          <h3>Currently Table Contain {foundUsers.length} Records <br/> Total Selected Debt is {getFormattedPrice(total)}</h3>
        )}
        <input
          type="search"
          value={name}
          id={style.myInput}
          onChange={filter}
          placeholder="Search for names.."
        />
        <table className={style.userTable}>
          <thead>
            <tr>
              <th>All</th>
              <th>Avator</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Gender</th>
              <th>Date Of Birth</th>
              <th>{getFormattedPrice(total)}</th>
              <th>
                {isediting ? (
                  <Button onClick={disableEdit}>Done</Button>
                ) : (
                  <Button onClick={enableEdit}>Edit</Button>
                )}
              </th>
            </tr>
          </thead>
          <tbody>
            {foundUsers && foundUsers.length > 0 ? (
              foundUsers.map((user) => (
                <tr key={user.id}>
                  <td>
                    <input
                      type="checkbox"
                      id={`custom-checkbox-${user.id}`}
                      name={name}
                      value={name}
                      checked={checkedState[user.id]}
                      onChange={() => handleOnChange(user.id)}
                    />
                  </td>
                  <td> {user.first_name}</td>
                  <td> {user.last_name}</td>
                  <td> {user.email}</td>
                  <td> {user.gender}</td>
                  <td>{user.DateOfBirth}</td>
                  <td>{user.Debt} $</td>
                  <td>
                    {isediting && (
                      <Button onClick={() => props.Ondelete(user.id)}>
                        Delete
                      </Button>
                    )}
                  </td>
                </tr>
              ))
            ) : (
              <h1>No results found!</h1>
            )}
          </tbody>
        </table>
      </div>
    </Card>
  );
}

export default SearchUserList;
