    <Card className={classes.input}>
      <form onSubmit={submitHandler}>
        <label htmlFor="FirstName">FirstName</label>
        <input value={firstName} onChange={FirstNameChangeHandler} id="FirstName" type="text" />
        <label htmlFor="LastName">LastName</label>
        <input value={lastName} onChange={LastNameChangeHandler} id="LastName" type="text" />
        <label htmlFor="Email">Email</label>
        <input value={email} onChange={EmailChangeHandler} id="Email" type="text" />
        <label htmlFor="Gender">Gender</label>
        <input value={gender} onChange={GenderChangeHandler} id="Gender" type="text" />
        <label htmlFor="DateofBirth">Date of Birth</label>
        <input value={DateOfBirth}onChange={DateofBirthChangeHandler} id="DateofBirth" type="date"/>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
