import React from "react";
import { Fragment } from "react/cjs/react.production.min";
import Button from "../../UI/Button";
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css

const EditableOnlyRow = ({ user, isediting, Ondelete,editFormChangeHandler, }) => {

  const submit = () => {
    confirmAlert({
      title: "Delte User",
      message: ` Are you sure to do delete  ${user.first_name} ?`,
      buttons: [
        {
          label: "Yes",
          onClick: () => Ondelete(user.id),
        },
        {
          label: "No",
          onClick: () => console.log("no"),
        },
      ],
    });
  };
  return (
    <tr>
      <td />
      <td>
        <input
          type="text"
          required
          placeholder="first_name"
          name="first_name"
        />
      </td>
      <td>
        <input type="text" required placeholder="last_name" name="last_name" />
      </td>
      <td>
        <input type="text" required placeholder="time" name="time" />
      </td>
      <td>
        <input type="email" required placeholder="email" name="email" />
      </td>
      <td>
        <input type="text" required placeholder="Job" name="Job" />
      </td>
      <td>
        <input
          type="date"
          required
          placeholder="DateOfBirth"
          name="DateOfBirth"
        />
      </td>
      <td>
        <input
          type="number"
          min="0"
          required
          placeholder="donate2"
          name="donate"
        />
      </td>
      <td>
        {isediting && (
          <Fragment>
            {/* <Button onClick={() => Ondelete(user.id)}>Delete</Button> */}
            <Button onClick={submit}>Delete</Button>
            <Button
              onClick={() => {
                alert(`${user.first_name}  "Edit Button Clicked"`);
              }}
            >
              Save
            </Button>
          </Fragment>
          
        )}
      </td>
    </tr>
  );
};

export default EditableOnlyRow;
