import React, { useState } from "react";
import styles from "./addUser.module.css";

function AddUser(porps) {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");

  const nameChangeHandler = (event) => {
    setName(event.target.value);
  };
  const ageChangeHandler = (event) => {
    setAge(event.target.value);
  };

  const onsubmitform = (event) => {
    if(name!=null && age!=null){
    event.preventDefault();
      const formData = {
        userName: name,
        age: age,
      }
    };
    e
    
    porps.onsaveform(formData);
    setName("");
    setAge("");
  };

  return (
    <div className={styles.maindiv}>
      <form className={styles.usrform} onSubmit={onsubmitform}>
        <label className={styles.lblform}>Username</label>
        <input
          type="text"
          className={styles.inpform}
          onChange={nameChangeHandler}
          value={name}
        />
        <label className={styles.lblform}>Age (years)</label>
        <input
          type="number"
          min="18"
          step="1"
          className={styles.inpform}
          onChange={ageChangeHandler}
          value={age}
        />
        <button type="submit" className={styles.btnform}>
          Add
        </button>
      </form>
    </div>
  );
}

export default AddUser;
