import React, { useState } from "react";
import Card from "../UI/Card";
import ErroModal from "../UI/ErrorModal";
import styles from "./addUser.module.css";

function AddUser(porps) {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [error, setError] = useState();

  const nameChangeHandler = (event) => {
    setName(event.target.value);
  };
  const ageChangeHandler = (event) => {
    setAge(event.target.value);
  };

  const onsubmitform = (event) => {
    event.preventDefault();
    if (name.trim().length === 0) {
      setError({
        title: "Something went wrong",
        message: "Name is invalid",
      });
      return;
    }

    if (age.trim().length === 0 || +age < 0) {
      setError({
        title: "Something went wrong",
        message: "Age is invalid",
      });
      return;
    }

    const formData = {
      userName: name,
      age: age,
    };
    porps.onsaveform(formData);

    setName("");
    setAge("");
  };

  return (
    <section>
      {error && <ErroModal title={error.title} message={error.message} onConfirm/>}
      <Card>
        <form className={styles.usrform} onSubmit={onsubmitform}>
          <label htmlFor="userName" className={styles.lblform}>
            Username
          </label>
          <input
            id="userName"
            type="text"
            className={styles.inpform}
            onChange={nameChangeHandler}
            value={name}
          />
          <label htmlFor="age" className={styles.lblform}>
            Age (years)
          </label>
          <input
            id="age"
            type="number"
            // min="18"
            step="1"
            className={styles.inpform}
            onChange={ageChangeHandler}
            value={age}
          />
          <button type="submit" className={styles.btnform}>
            Add
          </button>
        </form>
      </Card>
    </section>
  );
}

export default AddUser;
