import React, { useState } from "react";
import AddUser from "./Components/User/addUser";
import styles from "./App.module.css";
import UserList from "./Components/UserList";

function App() {
  const [formfill, setFormFill] = useState([
    { userName: "Max", age: 25, id: "g1" },
    { userName: "Mas", age: 41, id: "g2" },
  ]);

  const submitHandler = (enteredData) => {
    const userlistnew = {
      ...enteredData,
      id: Math.random().toString(),
    };
    console.log("userlistnew");
    console.log(userlistnew);

    setFormFill((prevUserList) => {
      return [userlistnew, ...prevUserList];
    });
  };

  return (
    <div className={styles.main}>
      <AddUser onsaveform={submitHandler} />
      <UserList items={formfill} />
    </div>
  );
}

export default App;
